import React from 'react';
import NewsItem from './NewsItem';
import Spinner from './Spinner';
import PropTypes from 'prop-types';
import InfiniteScroll from 'react-infinite-scroll-component';

class News extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      article: [], // Initialize article as an empty array
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  async componentDidMount() {
    // Fetch initial news data
    await this.UpdateNews();
  }

  async UpdateNews() {
    // Fetch news data based on props
    // Update state after fetching data
    this.setState({ loading: true });
    // Fetch data asynchronously and update state
    // Example:
    // const data = await fetchData();
    // this.setState({ article: data.articles, totalResults: data.totalResults, loading: false });
  }

  fetchMoreData = async () => {
    // Fetch more data for infinite scrolling
    // Update state accordingly
  };

  render() {
    const { article, loading } = this.state;

    return (
      <div className="container my-3">
        <h1 className="text-center" style={{ margin: '35px 0px' }}>Top Headlines</h1>
        {loading && <Spinner />}
        <InfiniteScroll
          dataLength={article.length} // Check if article is defined before accessing length
          next={this.fetchMoreData}
          hasMore={article.length !== this.state.totalResults}
          loader={<Spinner />}
        >
          <div className="container">
            <div className="row">
              {article.map(element => (
                <div className="col-md-4 my-3" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title.slice(0, 45) : ''}
                    description={element.description ? element.description.slice(0, 80) : ''}
                    imageurl={element.urlToImage}
                    newsurl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                  />
                </div>
              ))}
            </div>
          </div>
        </InfiniteScroll>
      </div>
    );
  }
}

News.propTypes = {
  // Define propTypes for props used in the component
};

export default News;
